import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export function BuyBook(){
    const params=useParams();
    const [book,setBook]=useState({})
    const url = "http://localhost:8088/api/customers/buy/"+ params.id;

    const navigate=useNavigate();
        const getData = () => {
            const data = axios.get(url);
            data.then(resp => {setBook(resp.data);console.log(book)})
            .catch(error => console.log(error));
        }
        useEffect(() =>{
            getData();
        },[])
    
        const inputHandler=(e)=>{
            setBook((book)=>({
                ...book,
                [e.target.id]:e.target.value
            }))
        }

        const submitHandler=(e)=>{
            e.preventDefault();
            const data=axios.put(url,book);
            data.then(resp=>{
                navigate('/booklist')
            })
            .catch(error=>console.log(error));
        }

        return(
            <div>
                <h1>Buy Book</h1>
                <button className="btn btn-secondary" onClick={()=>navigate(-1)}>Go Back</button>
                <div className="row">
                    <div className="col-md-6 offset-3">
                        <form className="form" onSubmit={submitHandler}>
                        
                        <label>Id</label>
                        <input type = "number" id="id" placeholder="Enter Id"
                        value={book.id} className="form-control" onChange={inputHandler}/>
                        <label>Name</label>
                        <input type = "text" id="name" placeholder="Enter Name"
                        value={book.name} className="form-control" onChange={inputHandler}/>
                        <label>Email</label>
                        <input type = "text" id="email" placeholder="Enter Email"
                        value={book.email} className="form-control" onChange={inputHandler}/>
                        <label>Phone</label>
                        <input type = "number" id="phone" placeholder="Enter Phone"
                        value={book.phone} className="form-control" onChange={inputHandler}/>
                        <button className="btn btn-primary" typr="submit">Register</button>
                        </form>
                    </div>
                </div>
            </div>
        );
}
