import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export function BookList(props){
    const [books,setBooks]= useState([]);
    const url= "http://localhost:8088/api/books/";

    const navigate = useNavigate();
    const getData = () =>{
        const data = axios.get(url);
        data.then(resp => setBooks(resp.data))
        .catch(error => console.log(error));
    }

    const deleteBook=(id)=>{
        axios.delete(url+id)
        .then(resp=>{console.log(resp); getData()})
        .catch(error=>console.log(error));
    }
    useEffect(()=>{
        getData();
    }, [])

    const tabRow = books.map((book,index)=>{
        return (
            <tr key={index}>
                <td>{book.id}</td>
                <td>{book.title}</td>
                <td>{book.author}</td>
                <td>{book.price}</td>
                <td>{book.genre}</td>
                <td><button className="btn btn-primary">
                    onClick={() => navigate("/viewbook/" +book.id)}View</button>&nbsp;
                    <button className="btn btn-danger" 
                    onClick={()=>deleteBook(book.id)}>Delete</button>&nbsp;
                    <button className="btn btn-success"
                    onClick={() => navigate("/editbook/"+book.id)}>Edit</button>
                </td>
            </tr>
        )
    })
    return(
        <div>
            <h1>BookList Component</h1>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>Id</th><th>Title</th><th>Author</th><th>Price</th><th>Genre</th>
                    </tr>
                </thead>
                <tbody>
                    {tabRow}
                </tbody>
            </table>
        </div>
    )
}
